function createDataset(fields, constraints, sortFields) CHAVESIN

	var user = ASPASadminASPAS;
	var password = 'CHAVESINSENHAUSUARIOADMINCHAVESOUT';
	var newDataset = DatasetBuilder.newDataset();
	var numProcess = ASPASASPAS;
	var cardId = ASPASASPAS;
	var mensagem = ASPASASPAS;

	newDataset.addColumn(ASPASdocumentIdASPAS);
	newDataset.addColumn(ASPASdocumentDescriptionASPAS);
	newDataset.addColumn(ASPASversionASPAS);
	newDataset.addColumn(ASPASwebServiceMessageASPAS);

	try CHAVESIN
		var servico = ServiceManager.getService(ASPASCardServiceASPAS);
		var serviceHelper = servico.getBean();
		var service = serviceHelper.instantiate('fluig.ECMCardServiceService');
		var request = service.getCardServicePort();
		var ObjectFactory = serviceHelper.instantiate('fluig.ObjectFactory');

		if (constraints != null) CHAVESIN
			for (var i = 0; i < constraints.length; i++) CHAVESIN
				if (constraints[i].fieldName == ASPASnumProcessASPAS) CHAVESIN
					numProcess = constraints[i].initialValue.toString();     
				CHAVESOUTelse if (constraints[i].fieldName == ASPASmensagemASPAS) CHAVESIN     
					mensagem = constraints[i].initialValue.toString();     
				CHAVESOUTelse if (constraints[i].fieldName == ASPAScardIdASPAS) CHAVESIN     
					cardId = constraints[i].initialValue.toString();     
				CHAVESOUT
			CHAVESOUT
		CHAVESOUT

		log.info(ASPAS<<< ALTERACAO DO FORMULARIO ASPAS + cardId + ASPAS-ASPAS + ASPAS-ASPAS+ mensagem);
		var cardFieldDtoArray = ObjectFactory.createCardFieldDtoArray();

		//Seta o historico passado via parametro
		var cardFieldDto = ObjectFactory.createCardFieldDto();
		cardFieldDto.setField(ASPASMessageBoxASPAS);
		cardFieldDto.setValue(mensagem);
		cardFieldDtoArray.getItem().add(cardFieldDto);

		var retorno = request.updateCardData(1,user,password,cardId,cardFieldDtoArray); 

		var msg = retorno.getItem();

		var documentId = msg.get(0).getDocumentId();
		var documentDescription = msg.get(0).getDocumentDescription();
		var version = msg.get(0).getVersion();
		var webServiceMessage = msg.get(0).getWebServiceMessage();

		log.info(ASPASdsChatIP - ALTERACAO DE CARD_DATA - ASPAS + ASPAS webServiceMessage: ASPAS + webServiceMessage + ASPAS mensagem: ASPAS + mensagem)
		newDataset.addRow(new Array(documentId,documentDescription,version,webServiceMessage));

	CHAVESOUTcatch (e) CHAVESIN
		newDataset.addRow(new Array('','','',e));
		throw(e);
	CHAVESOUT
	return newDataset;
CHAVESOUT
